mapper.select:
SELECT A.* FROM ( SELECT ROWNUM AS RNUM, B.* FROM ( SELECT B.BOARD_NO, B.TITLE, B.CONTENT, B.WRITTEN_DATE, B.VIEWS, B.USER_NO, B.CODE_NO, B.FILE_GROUP_NUM, B.DELYN, U.NAME AS WRITER, (SELECT COUNT(*) FROM COMMENTS C WHERE C.BOARD_NO = B.BOARD_NO) AS COMMENTS_COUNT FROM BOOK_BOARD B JOIN USERS U ON (B.USER_NO = U.USER_NO) WHERE 1=1
AND B.TITLE LIKE '%' || #{searchKeyword} || '%'
AND B.CONTENT LIKE '%' || #{searchKeyword} || '%'
AND U.NAME LIKE '%' || #{searchKeyword} || '%'
AND (B.TITLE LIKE '%' || #{searchKeyword} || '%' OR B.CONTENT LIKE '%' || #{searchKeyword} || '%')
AND B.USER_NO = #{userNo}
AND B.CODE_NO = #{codeNo}
ORDER BY
B.WRITTEN_DATE DESC
B.WRITTEN_DATE ASC
B.VIEWS DESC
(SELECT COUNT(*) FROM COMMENTS C WHERE C.BOARD_NO = B.BOARD_NO) DESC
B.BOARD_NO DESC
) B
WHERE ROWNUM <= #{endRow}
) A
WHERE RNUM >= #{startRow}

mapper.update:
UPDATE BOOK_BOARD
   SET TITLE = #{title},
	   CONTENT = #{content},
       CODE_NO = #{codeNo},
       FILE_GROUP_NUM = #{fileGroupNum}
 WHERE BOARD_NO = #{boardNo}

UPDATE BOOK_BOARD
   SET DELYN = 'Y' 
 WHERE BOARD_NO = #{boardNo}

UPDATE BOOK_BOARD
   SET VIEWS = VIEWS + 1
 WHERE BOARD_NO = #{boardNo}
